    private void jBtMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtMostrarActionPerformed
        Multiplicacion_Recursiva multi = new Multiplicacion_Recursiva();
        int num = Integer.parseInt(jTextFNumero.getText());
        multi.Multiplicacion(num);
        jTextAResultado.setText(multi.cad);
    }//GEN-LAST:event_jBtMostrarActionPerformed

